-- Create the 'role' table
CREATE TABLE role (
  id INT AUTO_INCREMENT PRIMARY KEY,
  name VARCHAR(50) NOT NULL UNIQUE
);

-- Insert roles into the 'role' table

-- Create the 'user' table
CREATE TABLE user (
  id INT AUTO_INCREMENT PRIMARY KEY,
  username VARCHAR(255) NOT NULL UNIQUE,
  password VARCHAR(255) NOT NULL,
  role_id INT,
  FOREIGN KEY (role_id) REFERENCES role(id)
);

-- Tabla de enfermedades
CREATE TABLE disease (
 disease_id INT AUTO_INCREMENT PRIMARY KEY,
 name VARCHAR(255) UNIQUE NOT NULL,
 description TEXT NOT NULL
);

-- Tabla de síntomas
CREATE TABLE symptom (
 symptom_id INT AUTO_INCREMENT PRIMARY KEY,
 name VARCHAR(255) UNIQUE NOT NULL,
 severity INT NOT NULL
);

--tabla 'precaution'
CREATE TABLE precaution (
    precaution_id INT AUTO_INCREMENT PRIMARY KEY,
    precaution_text VARCHAR(255) NOT NULL,
    UNIQUE (precaution_text)
);

--tabla intermedia 'disease_precautions' para la relación muchos a muchos
CREATE TABLE disease_precautions (
 disease_id INT,
 precaution_id INT,
 FOREIGN KEY (disease_id) REFERENCES disease(disease_id) ON DELETE CASCADE,
 FOREIGN KEY (precaution_id) REFERENCES precaution(precaution_id) ON DELETE CASCADE,
 PRIMARY KEY (disease_id, precaution_id)
);


-- Tabla intermedia para la relación muchos a muchos entre enfermedad y síntomas
CREATE TABLE disease_symptom (
 disease_id INT,
 symptom_id INT,
 PRIMARY KEY (disease_id, symptom_id),
 FOREIGN KEY (disease_id) REFERENCES disease(disease_id) ON DELETE CASCADE,
 FOREIGN KEY (symptom_id) REFERENCES symptom(symptom_id) ON DELETE CASCADE
);

CREATE TABLE patient (
 id INT AUTO_INCREMENT PRIMARY KEY,
 username VARCHAR(255) NOT NULL UNIQUE,
 email VARCHAR(100) NOT NULL UNIQUE,
 FOREIGN KEY (username) REFERENCES user(username) ON DELETE CASCADE
);

CREATE TABLE diagnosis (
   id INT AUTO_INCREMENT PRIMARY KEY,
   patient_id INT,
   disease_id INT,
   date DATE NOT NULL,
   score DOUBLE NOT NULL DEFAULT 0,
   confidence DOUBLE NOT NULL DEFAULT 0,
   FOREIGN KEY (patient_id) REFERENCES patient(id) ON DELETE CASCADE,
   FOREIGN KEY (disease_id) REFERENCES disease(disease_id) ON DELETE CASCADE
);








